mapper.select:
select count(*)
from view
where view_article = #{artId}

select count(*)
from like_it
where like_article = #{artId}
  and like_user = #{userid}
  and like_status = 1;

select count(*)
from comment
where com_article = #{artId}
  and com_user = #{userid}
  and com_status = 1;

select count(*)
from collect
where col_article = #{artId}
  and col_user = #{userid}
  and col_status = 1;

mapper.update:
update like_it
set like_status = 1
where like_article = #{artId}
  and like_user = #{userid}
  and like_status = 2

update like_it
set like_status = 2
where like_article = #{artId}
  and like_user = #{userid}
  and like_status = 1

update collect
set col_status = 1
where col_article = #{artId}
  and col_user = #{userid}
  and col_status = 2

update collect
set col_status = 2
where col_article = #{artId}
  and col_user = #{userid}
  and col_status = 1
